Write-Output ""$profileFile = $profile.AllUsersCurrentHost$initials = Read-Host 'Enter your name initials'$fullName = Read-Host 'Enter your fullname'if( -not (Test-Path $profileFile )){    New-Item -ItemType File -Path $profileFile -Force}$dataFilePath = "C:\temp\datafile.txt"if ( -not (Test-Path $dataFilePath) ){    New-Item -ItemType File -Path $dataFilePath -Force    Out-File $dataFilePath -InputObject $initials    Out-File $dataFilePath -InputObject $fullName -Append}else {     $initials = (Get-Content $dataFilePath)[0]    $fullName = (Get-Content $dataFilePath)[1]}(Get-Item $dataFilePath -Force).Attributes = 'Hidden'$promptContent = "`$dateOutput = Get-Date -Format g``$hn = hostname``$initials = (Get-Content $dataFilePath)[0]``$fullName = (Get-Content $dataFilePath)[1]`function prompt{    `$IsAdmin` = (New-Object Security.Principal.WindowsPrincipal ([Security.Principal.WindowsIdentity]::GetCurrent())).IsInRole([Security.Principal.WindowsBuiltinRole]::Administrator)    if (`$isAdmin`)    {`$titleText` = 'Administrator: ' + `$hn` + '-' + `$fullName`}    else {`$titleText` = `$hn` + '-' + `$fullName`}    `$initials` + ' ' + `$dateOutput` +'> '    `$host.UI.RawUI.WindowTitle` = `$titleText`}"Add-Content -Path $profileFile -Value $promptContent$prefContent = "`$myPSPref = (Get-Host).PrivateData``$myPSPref.WarningForeGroundColor = 'Yellow'``$myPSPref.ErrorBackGroundColor   = 'DarkRed'``$myPSPref.ErrorForeGroundColor   = 'Yellow'`"Add-Content -Path $profileFile -Value $prefContent